function generate()   ts = [40,50,60]   ns = [10,20, 50, 100]   for n_nodes = ns      for tightness = ts         fname = ["file_",num2str(n_nodes),"_", num2str(tightness),".txt"];         fid = fopen(fname,"w");         mat = zeros(n_nodes^2,2);         c = 0;         for i = 1:n_nodes            for j = i+1:n_nodes               if 100*rand()< tightness                  c = c+1;                  mat(c,1:2) = [i,j];               end            end         end         fprintf(fid, "%i %i\n", n_nodes, c);         for k = 1:c            fprintf(fid, "%i %i\n", mat(k,1), mat(k,2));         end         fclose(fid);      end   endend